import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { useEffect } from 'react';
import { Route, Routes, BrowserRouter } from "react-router-dom";

import Menu from './components/Menu';
import Home from './pages/Home';
import About from './pages/About';
import Cases from './pages/Cases';
import Contact from './pages/Contact';
import ErrorPage from './pages/ErrorPage';

function App() {

    useEffect(() => {
        document.title = 'Home';
    }, []);

    // Get the URL of the current page
    var currentUrl = "http://localhost:3000";

    // Get the navigation element
    var nav = document.getElementById("navigation");

    // Check if the current URL is the error page URL
    if (currentUrl == <ErrorPage />) {
    // Hide the navigation if the current page is the error page
    nav.style.display = "none";
    } else {
    // Show the navigation if the current page is not the error page
    nav.style.display = "block";
    }


    return (
        <div className="App">
            <Menu />
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Home/>} />
                    <Route path="about" element={<About/>} />
                    <Route path="cases" element={<Cases/>} />
                    <Route path="contact" element={<Contact/>} />
                    <Route path="/*" element={<ErrorPage/>} />
                </Routes>
            </BrowserRouter>
        </div>
    );
}

export default App;
